theme_scss:
@import url("https://fonts.googleapis.com/css2?family=Merriweather:wght@300;400;500;600;700;800&display=swap");
// backward compatibility. deprecated in v15
$font-family-sans-serif: "Merriweather", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif;

// override font stack if custom font is set in website theme
:root {
	--font-stack: "Merriweather", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif !important;
}
$enable-shadows: false;
$enable-gradients: false;
$enable-rounded: true;

